declare module '*.json' {
  const content: JSON
  export default content
}

declare module '*.yml' {
  const content: string
  export default content
}

declare module '*.png' {
  const content: string
  export default content
}

declare module '*.txt' {
  const content: string
  export default content
}

declare module '*.css' {
  const style: any
  export default style
}

declare module '*.scss' {
  const style: any
  export default style
}
